<?phpinclude 'functions.php';if(isset($_POST["Import_csv"])){	$filename=$_FILES["file"]["tmp_name"]; 	$row = 1;	$skus = array();	$backers = array();	$file_data = array();		if($_FILES["file"]["size"] > 0) {		if (($handle = fopen($filename, "r")) !== FALSE) {			while (($data = fgetcsv($handle, 1000, ",")) !== FALSE) {				if($row>1){					$num = count($data);					$file_data[]=$data;										if(!isset($backers[$data[0]])) 						$backers[$data[0]] = ['email'=>$data[2], 'phone'=>$data[3], 'name'=>stripAccents($data[5]), 'address'=>stripAccents($data[6]), 'address2'=>stripAccents($data[7]), 'city'=>stripAccents($data[8]), 'province'=>stripAccents($data[9]), 'postalcode'=>preg_replace("/[^a-zA-Z0-9]/", "", $data[10]), 'country'=>$data[11], 'notes'=>$data[12] ];					$backers[$data[0]]['sku'][$data[20]] = $data[23];										if(!isset($skus[$data[20]])) 						$skus[$data[20]] = ['title'=>$data[21], 'cost'=>$data[22], 'weight'=>$data[24], 'length'=>$data[25], 'width'=>$data[26], 'height'=>$data[27]];				}				$row++;			}						fclose($handle);		}	}}	?><!DOCTYPE html><html lang="en"><head>	<link rel="stylesheet" type="text/css" href="style.css"></head><body>	<div class="row">		<form class="form-horizontal" action="#" method="post" name="upload_csv" enctype="multipart/form-data">			<fieldset>				<legend>Carrier chooser</legend>				<!-- File Button -->				<div class="form-group">					<label class=" control-label" for="filebutton">Select File</label>					<div class="">						<input type="file" name="file" id="file" class="input-large">					</div>				</div>				<!-- Button -->				<div class="center">					<button type="submit" id="import" name="Import_csv" class="btn-bleu" data-loading-text="Loading...">Import</button>				</div>			</fieldset>		</form>	</div>	<?php if($backers){ ?>	<div class="row"><small><pre>	<?php print_r($backers); ?>		</pre></small></div><?php } ?>	<div class="row center">		<button type="button" 